  `wx` varchar(64) DEFAULT NULL,
  `createtime` datetime DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP,
  `lastlogintime` datetime DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP,
