-- ----------------------------
-- Procedure structure for sp_createTable
-- ----------------------------
DROP PROCEDURE IF EXISTS `sp_createTable`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_createTable`(out p_name VARCHAR(10), out p_fieldstr VARCHAR(10))
BEGIN
    DECLARE sql_text VARCHAR(50); 
        DROP TABLE IF EXISTS p_name;
        SET sql_text=CONCAT('CREATE TABLE ',p_name,p_fieldstr);
        SELECT sql_text; 
        SET @sql_text=sql_text;
        PREPARE stmt FROM @sql_text;
        EXECUTE stmt;
    DEALLOCATE PREPARE stmt;  
  END
;;
DELIMITER ;
SET FOREIGN_KEY_CHECKS=1;
